import { useState } from 'react';

export default function useToken() {
    const getToken = () => {
        const tokenString = localStorage.getItem('token');
        // It is used to decode string to Json object
        console.log(tokenString);
        if(tokenString==null){
          return null;
        }

        const tokenDecodedString = atob(tokenString);
        const userToken = JSON.parse(tokenDecodedString);
        return userToken==null?null:userToken
      };
  const [token, setToken] = useState(getToken());
  const saveToken = userToken => {
    if(typeof(userToken)!='undefined' && userToken!=null){
      localStorage.setItem('token', userToken);
      // It is used to decode string to Json object
      const tokenDecodedString = atob(userToken);
      userToken = JSON.parse(tokenDecodedString);
      // set the JSON object
      userToken==null?setToken(null):setToken(userToken);
    }else{
      setToken(null);
    }
  };
  return {
    setToken: saveToken,
    token
  }
}
