import logo from './logo.svg';
import './App.css';
import Login from './component/Login';
import useToken from './component/UseToken';
import MyAccountsApp from './component/MyAccountsApp';
import ElogApps from './component/ElogApps';
import { BrowserRouter as Router, Route, Switch, Link } from "react-router-dom";

function App() {

  const { token, setToken } = useToken();

  if(token==null) {
    return(   
      <div>   
      <Router>
    <Switch> 
    <Route exact path='*' render={(props) => <Login setToken={setToken} {...props} /> } />
    </Switch>
    </Router>
    </div>
    );
  }

  function logOut(){
    localStorage.removeItem("token");
    setToken(null);
  }

  return (
    <div>
      <center>
      <Router>
        <Switch>
          <Route exact path='/'>
            <be/>
            <h1>Welcome to Fuji Films Apps Single Sing On</h1>
        <table border="2">
          <tr>
          <th colSpan="2">Welcome to the FujiFilms Microservices</th>
          </tr>
          <tr>
            <td>
              1. <Link to="/myaccountms">Click Here</Link> to Go to MyAccount App
            </td>
          </tr>
          <tr>
          <td>
              2. <Link to="/elogms">Click Here</Link> to Go to Elog App
            </td>
          </tr>
          <tr>
          <td>
              <button style={{width : '100%'}} onClick={logOut}>Logout</button>
            </td>
          </tr>
        </table>
      </Route>
      <Route path="/myaccountms">
        <MyAccountsApp></MyAccountsApp>
      </Route>
      <Route path="/elogms">
        <ElogApps></ElogApps>
      </Route>
      </Switch>
      </Router>
      </center>
    </div>
  );
}

export default App;
